contracts/adapters/ERC20PaymentAdapter.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IPaymentAdapter.sol";

/**
 * @title ERC20PaymentAdapter
 * @dev Standard ERC20 token payment adapter for the escrow system
 */
contract ERC20PaymentAdapter is IPaymentAdapter, Ownable {
    using SafeERC20 for IERC20;

    mapping(address => bool) public supportedTokens;
    mapping(address => uint256) public minimumAmounts;

    constructor() {}

    function processPayment(
        address from,
        address to,
        uint256 amount,
        address token,
        bytes calldata metadata
    ) external override returns (bool success) {
        (bool isValid, string memory reason) = _validatePayment(from, to, amount, token);
        require(isValid, reason);

        IERC20(token).safeTransferFrom(from, to, amount);

        emit PaymentProcessed(from, to, amount, token, metadata);
        return true;
    }

    function validatePayment(
        address from,
        address to,
        uint256 amount,
        address token
    ) external view override returns (bool isValid, string memory reason) {
        return _validatePayment(from, to, amount, token);
    }

    function _validatePayment(
        address from,
        address to,
        uint256 amount,
        address token
    ) internal view returns (bool isValid, string memory reason) {
        if (!supportedTokens[token]) {
            return (false, "Token not supported");
        }

        if (from == address(0) || to == address(0)) {
            return (false, "Invalid addresses");
        }

        if (amount == 0) {
            return (false, "Amount must be greater than zero");
        }

        if (amount < minimumAmounts[token]) {
            return (false, "Amount below minimum");
        }

        if (IERC20(token).balanceOf(from) < amount) {
            return (false, "Insufficient balance");
        }

        return (true, "");
    }

    function isTokenSupported(address token) external view override returns (bool supported) {
        return supportedTokens[token];
    }

    function getAdapterInfo() external pure override returns (
        string memory name,
        string memory version,
        string[] memory supportedFeatures
    ) {
        string[] memory features = new string[](3);
        features[0] = "ERC20_TRANSFERS";
        features[1] = "BALANCE_VALIDATION";
        features[2] = "MINIMUM_AMOUNTS";

        return ("ERC20PaymentAdapter", "1.0.0", features);
    }

    function addSupportedToken(address token, uint256 minimumAmount) external onlyOwner {
        supportedTokens[token] = true;
        minimumAmounts[token] = minimumAmount;
    }

    function removeSupportedToken(address token) external onlyOwner {
        supportedTokens[token] = false;
        delete minimumAmounts[token];
    }
}

contracts/adapters/IPaymentAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

/**
 * @title IPaymentAdapter
 * @dev Interface for payment adapters supporting different token types and payment rails
 * @notice Enables future expansion with new payment methods without core contract changes
 */
interface IPaymentAdapter {
    /**
     * @dev Emitted when a payment is processed
     */
    event PaymentProcessed(
        address indexed from,
        address indexed to,
        uint256 amount,
        address indexed token,
        bytes metadata
    );

    /**
     * @dev Process payment from sender to receiver
     */
    function processPayment(
        address from,
        address to,
        uint256 amount,
        address token,
        bytes calldata metadata
    ) external returns (bool success);

    /**
     * @dev Validate payment parameters before processing
     */
    function validatePayment(
        address from,
        address to,
        uint256 amount,
        address token
    ) external view returns (bool isValid, string memory reason);

    /**
     * @dev Check if a token is supported by this adapter
     */
    function isTokenSupported(address token) external view returns (bool supported);

    /**
     * @dev Get adapter-specific information
     */
    function getAdapterInfo() external view returns (
        string memory name,
        string memory version,
        string[] memory supportedFeatures
    );
}

contracts/adapters/PaymentAdapterRegistr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IPaymentAdapter.sol";

/**
 * @title PaymentAdapterRegistry
 * @dev Registry for managing multiple payment adapters
 * @notice Enables dynamic addition of new payment methods
 */
contract PaymentAdapterRegistry is Ownable {
    mapping(string => address) public adapters;
    mapping(address => bool) public authorizedAdapters;
    string[] public adapterNames;

    event AdapterRegistered(string indexed name, address indexed adapter);
    event AdapterRemoved(string indexed name, address indexed adapter);
    event AdapterAuthorized(address indexed adapter, bool authorized);

    /**
     * @dev Register a new payment adapter
     */
    function registerAdapter(string memory name, address adapter) external onlyOwner {
        require(adapter != address(0), "Invalid adapter address");
        require(adapters[name] == address(0), "Adapter already registered");

        adapters[name] = adapter;
        authorizedAdapters[adapter] = true;
        adapterNames.push(name);

        emit AdapterRegistered(name, adapter);
        emit AdapterAuthorized(adapter, true);
    }

    /**
     * @dev Remove a payment adapter
     */
    function removeAdapter(string memory name) external onlyOwner {
        address adapter = adapters[name];
        require(adapter != address(0), "Adapter not found");

        delete adapters[name];
        authorizedAdapters[adapter] = false;

        // Remove from names array
        for (uint i = 0; i < adapterNames.length; i++) {
            if (keccak256(bytes(adapterNames[i])) == keccak256(bytes(name))) {
                adapterNames[i] = adapterNames[adapterNames.length - 1];
                adapterNames.pop();
                break;
            }
        }

        emit AdapterRemoved(name, adapter);
        emit AdapterAuthorized(adapter, false);
    }

    /**
     * @dev Get adapter by name
     */
    function getAdapter(string memory name) external view returns (address) {
        return adapters[name];
    }

    /**
     * @dev Check if adapter is authorized
     */
    function isAuthorized(address adapter) external view returns (bool) {
        return authorizedAdapters[adapter];
    }

    /**
     * @dev Get all registered adapter names
     */
    function getAllAdapterNames() external view returns (string[] memory) {
        return adapterNames;
    }

    /**
     * @dev Get adapter count
     */
    function getAdapterCount() external view returns (uint256) {
        return adapterNames.length;
    }
}

